package com.studyonyourown;

public class class9point3 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
      for (int i=1; i<7; i++) {
    	  for (int a=1; a<i; a++) {
    		  System.out.print(a);
    	  }System.out.println();
    	  
      }for (int b=7; b>1; b--) {
    	  for (int c=1; c<b; c++) {
    		  System.out.print(c);
    	  }System.out.println();
      }
	/*
	 * 	
	 */
		for (int d=1; d<7; d++) {
			for (int e=1; e<d; e++) {
				System.out.print("*");
			
		}System.out.println();
		}
		 for(int f=7; f>1; f--) {
			for(int g=1; g<f; g++){
				System.out.print("*");
			}System.out.println();
		}
	
	}
}
